import Grid from "@material-ui/core/Grid";
import Slider from "@material-ui/core/Slider";
import TextField from "@material-ui/core/TextField";
import React from "react";
import Card from "@material-ui/core/Card";
import CardActions from "@material-ui/core/CardActions";
import CardContent from "@material-ui/core/CardContent";
import Button from "@material-ui/core/Button";
import Typography from "@material-ui/core/Typography";
import "./3slider.css";

export default function OutlinedCard(props) {

  let {userData, setUserData, setStep} = props;


  const handleChange = (e, newValue) => {
    setUserData({ ...userData, dailyBudget : newValue })
  };

  var sliderValue = userData.dailyBudget || 0;

  const lead = sliderValue / 5;
  const lod = (sliderValue * 2)/ 5;



  return (
    <div>
      <div style={{ display: "flex" }}>
        <div className="left-side">
          <h3>Lets Talk about the budget</h3>
          <Typography id="continuous-slider" gutterBottom>
            Daily Budget
          </Typography>

          <Grid container spacing={1}>
            <Grid item></Grid>

            <Grid item xs>
              <Slider
                value={sliderValue}
                onChange={handleChange}
                aria-labelledby="continuous-slider"
                max={1000}
                color='primary'
                valueLabelDisplay="on"
              />
            </Grid>
            <Grid item></Grid>
          </Grid>


        </div>
        <div className="right-side">
          <Card variant="outlined" color="textSecondary" style={{backgroundColor: "white"}}>
            <CardContent>
              <div>

                <TextField
                type="text"
                disabled="true"
                value={ `Leads per day: ${Math.ceil(lead)}-${Math.ceil(lod)}`}
                onChange={(e) =>
                  setUserData({ ...userData, perDayLead: e.target.value })
                }

              />

                <TextField
                type="text"
                disabled="true"

                value={ `Cost per Lead: ${lead / 4}-${lod / 4}`}
                onChange={(e) =>
                  setUserData({ ...userData, perLeadCost: e.target.value })
                }

              />

              <TextField
              type="text"
              disabled="true"

              value={ sliderValue}
              onChange={(e) =>
                setUserData({ ...userData, sliderValue: e.target.value })
              }

            />

                <TextField
                type="text"
                disabled="true"
                value={userData["startdate"]}
                onChange={(e) =>
                    setUserData({ ...userData, startdate: e.target.value })
                  }
                  placeholder="Date"
                />{" "}
              </div>
              <TextField
                type="text"
                disabled="true"
                value={userData["enddate"]}
                onChange={(e) =>
                  setUserData({ ...userData, enddate: e.target.value })
                }
                placeholder="No end Date"
              />{" "}

            </CardContent>

            <CardActions></CardActions>
          </Card>
        </div>
      </div>
      <div className="sola">
        <Button size="large" onClick={() => setStep(2)}>
          Previous{" "}
        </Button>

        <Button size="large" onClick={() => setStep(4)}>
          Next{" "}
        </Button>
      </div>
    </div>
  );
}
